744     template<typename T>
745     void Matrix2D<T>::setDeleter(const std::function<void(T &)> &function) {
746         _deleter = function;
747     }
748 
749     template<typename T>
750     Matrix2D<T>::Matrix2D(uint32_t row, uint32_t col, const std::function<void(T &)> &deleter)
751             : _row(row), _col(col), _deleter(deleter) {
752         _datas.resize(_row * _col);
753     }
754 
755     template<typename T>
756     Matrix2D<T>::Matrix2D(uint32_t row, uint32_t col, const T &value, const std::function<void(T &)> &deleter)
757             : _row(row), _col(col), _deleter(deleter) {
758         _datas.resize(_row * _col);
759         fill(value);
760     }
761 
762     template<typename T>
763     Matrix2D<T>::Matrix2D(const Matrix2D<T> &matrix)
764             : _row(matrix._row), _col(matrix._col), _deleter(matrix._deleter) {
765         _datas = matrix._datas;
766     }
767 
768     template<typename T>
769     Matrix2D<T>::~Matrix2D() {
770         if (_deleter) {
771             for (auto &_d: _datas) {
772                 _deleter(_d);
773             }
774         }
775     }
