777     template<typename T>
778     void Matrix2D<T>::fill(const T &value) {
779         std::fill(_datas.begin(), _datas.end(), value);
780     }
781 
782     template<typename T>
783     bool Matrix2D<T>::fillN(const Matrix2D::Position &start, const Matrix2D::Position &end, const T &value) {
784         auto st = start.row * _col + start.col, ed = end.row * _col + end.col;
785         if (ed >= _datas.size()) {
786             SDL_Log("[ERROR] One of the specified position is not valid!");
787             return false;
788         }
789         std::fill(_datas.begin() + st, _datas.begin() + ed, value);
790         return true;
791     }
792 
793     template<typename T>
794     void Matrix2D<T>::resize(uint32_t line, uint32_t col) {
795         if (_row * _col != line * col) _datas.resize(line * col);
796         _row = line;
797         _col = col;
798     }
