800     template<typename T>
801     T &Matrix2D<T>::at(uint32_t row, uint32_t col) {
802         auto idx = row * _col + col;
803         if (idx >= _datas.size()) throw std::out_of_range("[FATAL] The specified position is out of range!");
804         return _datas.at(idx);
805     }
806 
807     template<typename T>
808     const T &Matrix2D<T>::get(uint32_t row, uint32_t col) {
809         auto idx = row * _col + col;
810         if (idx >= _datas.size()) throw std::out_of_range("[FATAL] The specified position is out of range!");
811         return _datas.at(idx);
812     }
