814     template<typename T>
815     uint32_t Matrix2D<T>::rows() const {
816         return _row;
817     }
818 
819     template<typename T>
820     uint32_t Matrix2D<T>::cols() const {
821         return _col;
822     }
823 
824     template<typename T>
825     Matrix2D<T> Matrix2D<T>::operator+(const Matrix2D<T> &other) const {
826         if ((_row == other._row) && (_col == other._col)) {
827             Matrix2D<T> _ret(_row, _col);
828             for (size_t _idx = 0; _idx < _datas.size(); ++_idx) {
829                 _ret[_idx] = _datas[_idx] + other._datas[_idx];
830             }
831             return _ret;
832         } else {
833             SDL_Log("[ERROR] Matrix dimensions mismatch!\nOriginal: (%d, %d), Specified: (%d, %d)",
834                     _row, _col, other._row, other._col);
835             return Matrix2D<T>();
836         }
837     }
838 
839     template<typename T>
840     Matrix2D<T> Matrix2D<T>::operator-(const Matrix2D<T> &other) const {
841         if ((_row == other._row) && (_col == other._col)) {
842             Matrix2D<T> _ret(_row, _col);
843             for (size_t _idx = 0; _idx < _datas.size(); ++_idx) {
844                 _ret[_idx] = _datas[_idx] - other._datas[_idx];
845             }
846             return _ret;
847         } else {
848             SDL_Log("[ERROR] Matrix dimensions mismatch!\nOriginal: (%d, %d), Specified: (%d, %d)",
849                     _row, _col, other._row, other._col);
850             return Matrix2D<T>();
851         }
852     }
853 
854     template<typename T>
855     Matrix2D<T> Matrix2D<T>::operator*(const Matrix2D<T> &other) const {
856         if (_col != other._row) {
857             SDL_Log("[ERROR] Matrix dimensions incompatible for multiplication!");
858             return Matrix2D<T>();
859         }
860         if constexpr (!std::is_integral_v<std::decay_t<T>> &&
861                       !std::is_floating_point_v<std::decay_t<T>>) {
862             static_assert(!std::is_integral_v<std::decay_t<T>> &&
863                           !std::is_floating_point_v<std::decay_t<T>>,
864                           "[FATAL] Can't support the current data type!");
865         }
866         Matrix2D<T> result(_row, other._col, other._deleter);
867         for (size_t i = 0; i < _row; ++i) {
868             for (size_t k = 0; k < _col; ++k) {
869                 const T &a_ik = _datas[i * _col + k];
870                 for (size_t j = 0; j < other._col; ++j) {
871                     result[i * other._col + j] += a_ik * other._datas[k * other._col + j];
872                 }
873             }
874         }
875         return result;
876     }
