878     template<typename T>
879     bool Matrix2D<T>::operator==(const Matrix2D<T> &other) const {
880         if (_row != other._row || _col != other._col) return false;
881         for (size_t i = 0; i < _datas.size(); ++i) {
882             if (_datas[i] != other._datas[i]) return false;
883         }
884         return true;
885     }
