902     T &Matrix2D<T>::operator()(uint32_t row, uint32_t col) {
903         auto idx = row * _col + col;
904         if (idx >= _datas.size()) throw std::out_of_range("[FATAL] The specified position is out of range!");
905         return _datas[idx];
