908     template<typename T>
909     void Matrix2D<T>::add(T &value, const std::function<void(T &, T &)> &function) {
910         std::for_each(_datas.begin(), _datas.end(), [&function, &value](T &v) {
911             if (function) {
912                 function(v, value);
913             } else if constexpr (std::is_integral_v<std::decay_t<T>> ||
914                                  std::is_floating_point_v<std::decay_t<T>>) {
915                 v += value;
916             } else {
917                 SDL_Log("[ERROR] Unsupported data type!\np.s: Did you forget to specify how to add values?");
918             }
919         });
920     }
