997     template<typename T>
998     void Matrix2D<T>::times(const Matrix2D<T> &other, const std::function<void(T &, const T &)> &function) {
999         if ((_row == other._row) && (_col == other._col)) {
1000             if (function) {
1001                 for (size_t i = 0; i < _datas.size(); ++i) {
1002                     function(_datas[i], other._datas[i]);
1003                 }
1004             } else if constexpr (std::is_integral_v<std::decay_t<T>> ||
1005                                  std::is_floating_point_v<std::decay_t<T>>) {
1006                 for (size_t i = 0; i < _datas.size(); ++i) {
1007                     _datas[i] *= other._datas[i];
1008                 }
1009             } else {
1010                 SDL_Log("[ERROR] Unsupported data type!\n"
1011                         "p.s: Did you forget to specify how to times values?");
1012             }
1013         } else {
1014             SDL_Log("[ERROR] Matrix dimensions mismatch!\nOriginal: (%u, %u), Specified: (%u, %u)",
1015                     _row, _col, other._row, other._col);
1016         }
1017     }
