1019     template<typename T>
1020     void Matrix2D<T>::multiply(const Matrix2D<T> &other) {
1021         if (_col != other._row) {
1022             SDL_Log("[ERROR] Matrix dimensions incompatible for multiplication!");
1023             return;
1024         }
1025         std::vector<T> result(_row * other._col);
1026         for (size_t i = 0; i < _row; ++i) {
1027             for (size_t k = 0; k < _col; ++k) {
1028                 const T &a_ik = _datas[i * _col + k];
1029                 for (size_t j = 0; j < other._col; ++j) {
1030                     result[i * other._col + j] += a_ik * other._datas[k * other._col + j];
1031                 }
1032             }
1033         }
1034         _datas = std::move(result);
1035         _col = other._col;
1036     }
1037 
1038     template<typename T>
1039     void Matrix2D<T>::transpose() {
1040         if (!_row || !_col) return;
1041         if (_row == _col) {
1042             for (size_t i = 0; i < _row; ++i) {
1043                 for (size_t j = i + 1; j < _col; ++j) {
1044                     std::swap(_datas[i * _col + j], _datas[j * _col + i]);
1045                 }
1046             }
1047             return;
1048         }
1049         std::vector<T> temp_vec;
1050         temp_vec.reserve(_row * _col);
1051         for (size_t j = 0; j < _col; ++j) {
1052             for (size_t i = 0; i < _row; ++i) {
1053                 temp_vec.push_back(_datas[i * _col + j]);
1054             }
1055         }
1056         std::swap(_datas, temp_vec);
1057         std::swap(_row, _col);
1058     }
1059 
1060     template<typename T>
1061     void Matrix2D<T>::reverse(bool reverse_row, bool reverse_col) {
1062         if (reverse_row && reverse_col) {
1063             const size_t M_SIZE = _datas.size() / 2;
1064             size_t _idx = 0, _last_idx = _datas.size() - 1;
1065             for (size_t i = 0; i < M_SIZE; ++i) {
1066                 std::swap(_datas[_idx++], _datas[_last_idx--]);
1067             }
1068         } else if (reverse_col) {
1069             const size_t M_SIZE_IN_COL = _col / 2;
1070             for (size_t i = 0; i < _row; ++i) {
1071                 for (size_t j = 0; j < M_SIZE_IN_COL; ++j) {
1072                     std::swap(_datas[i * _col + j], _datas[i * _col + _col - j - 1]);
1073                 }
1074             }
1075         } else if (reverse_row) {
1076             const size_t M_SIZE_IN_LINE = _row / 2;
1077             for (size_t i = 0; i < _col; ++i) {
1078                 for (size_t j = 0; j < M_SIZE_IN_LINE; ++j) {
1079                     std::swap(_datas[j * _col + i], _datas[(_row - j - 1) * _col + i]);
1080                 }
1081             }
1082         }
1083     }
1084 
1085     template<typename T>
1086     void Matrix2D<T>::rotate(bool turn_right) {
1087         if (!_row || !_col) return;
1088         std::vector<T> temp;
1089         temp.reserve(_row * _col);
1090         if (turn_right) {
1091             for (size_t c = 0; c < _col; ++c) {
1092                 for (size_t r = 0; r < _row; ++r) {
1093                     temp.emplace_back(_datas[(_row - r - 1) * _col + c]);
1094                 }
1095             }
1096         } else {
1097             for (size_t c = 0; c < _col; ++c) {
1098                 for (size_t r = 0; r < _row; ++r) {
1099                     temp.emplace_back(_datas[r * _col + (_col - c - 1)]);
1100                 }
1101             }
1102         }
1103         std::swap(temp, _datas);
1104         if (_row != _col) std::swap(_row, _col);
1105     }
