1107     template<typename T>
1108     Matrix2D<T> Matrix2D<T>::splitRows(uint32_t start_row, uint32_t end_row) {
1109         if (start_row == end_row) {
1110             SDL_Log("[ERROR] The specified start row and end row cannot be the same!");
1111             return Matrix2D<T>();
1112         }
1113         if (end_row > _row) {
1114             SDL_Log("[ERROR] The specified end row exceeds the total rows of the original matrix!");
1115             return Matrix2D<T>();
1116         }
1117         if (start_row > end_row) std::swap(start_row, end_row);
1118         auto new_rows = end_row - start_row;
1119         Matrix2D<T> _ret(new_rows, _col);
1120         for (size_t i = 0; i < new_rows; ++i) {
1121             for (size_t j = 0; j < _col; ++j) {
1122                 _ret[i * _col + j] = _datas[(start_row + i) * _col + j];
1123             }
1124         }
1125         return _ret;
1126     }
1127 
1128     template<typename T>
1129     Matrix2D<T> Matrix2D<T>::splitCols(uint32_t start_col, uint32_t end_col) {
1130         if (start_col == end_col) {
1131             SDL_Log("[ERROR] The specified start col and end col cannot be the same!");
1132             return Matrix2D<T>();
1133         }
1134         if (end_col > _col) {
1135             SDL_Log("[ERROR] The specified end col exceeds the total cols of the original matrix!");
1136             return Matrix2D<T>();
1137         }
1138         if (start_col > end_col) std::swap(start_col, end_col);
1139         auto new_cols = end_col - start_col;
1140         Matrix2D<T> _ret(_row, new_cols);
1141         for (size_t i = 0; i < _row; ++i) {
1142             for (size_t j = 0; j < new_cols; ++j) {
1143                 _ret[i * new_cols + j] = _datas[i * _col + j + start_col];
1144             }
1145         }
1146         return _ret;
1147     }
1148 
1149     template<typename T>
1150     Matrix2D<T> Matrix2D<T>::split(uint32_t rows, uint32_t cols, const Matrix2D::Position &start_pos) {
1151         if (start_pos.row >= _row || start_pos.col >= _col) {
1152             SDL_Log("[ERROR] The specified start position is not valid!");
1153             return Matrix2D();
1154         }
1155         Matrix2D<T> _ret(rows, cols);
1156         auto size = rows * cols;
1157         auto start_index = start_pos.row * _col + start_pos.col;
1158         auto elapsed = _datas.size() - start_index;
1159         bool is_less = elapsed < size;
1160         if (!is_less) {
1161             for (size_t i = 0; i < _ret._datas.size(); ++i) {
1162                 _ret[i] = _datas[start_index++];
1163             }
1164         } else {
1165             for (size_t i = 0; i < elapsed; ++i) {
1166                 _ret[i] = _datas[start_index++];
1167             }
1168             for (size_t i = elapsed; i < _ret._datas.size(); ++i) {
1169                 _ret[i] = {};
1170             }
1171         }
1172         return _ret;
1173     }
1174 
1175     template<typename T>
1176     Matrix2D<T> Matrix2D<T>::split(Matrix2D::Position start_pos,
1177                                    Matrix2D::Position end_pos) {
1178         if (start_pos > end_pos) {
1179             auto temp = start_pos;
1180             start_pos = end_pos;
1181             end_pos = temp;
1182         }
1183         if (end_pos.row >= _row) {
1184             end_pos.row = _row - 1;
1185         }
1186         if (end_pos.col >= _col) {
1187             end_pos.col = _col - 1;
1188         }
1189         auto rows = end_pos.row - start_pos.row + 1;
1190         auto cols = end_pos.col - start_pos.col + 1;
1191         Matrix2D<T> _ret(rows, cols);
1192         for (size_t i = 0; i < rows; ++i) {
1193             for (size_t j = 0; j < cols; ++j) {
1194                 _ret[i * _ret._col + j] = _datas[(start_pos.row + i) * _col + j + start_pos.col];
1195             }
1196         }
1197         return _ret;
1198     }
