1200     template<typename T>
1201     Matrix2D<T> Matrix2D<T>::inverse() {
1202         if (_row != _col) {
1203             SDL_Log("[ERROR] The specified matrix size is not matched!");
1204             return Matrix2D<T>();
1205         }
1206         if constexpr (!std::is_integral_v<std::decay_t<T>> &&
1207                       !std::is_floating_point_v<std::decay_t<T>>) {
1208             static_assert(!std::is_integral_v<std::decay_t<T>> &&
1209                           !std::is_floating_point_v<std::decay_t<T>>,
1210                           "[FATAL] Can't support the current data type!");
1211         }
1212         const uint32_t n = _row;
1213         Matrix2D<T> _ret(n, n);
1214         //  [A|I]
1215         Matrix2D<T> augmented(n, 2 * n);
1216         // 
1217         for (uint32_t i = 0; i < n; ++i) {
1218             for (uint32_t j = 0; j < n; ++j) {
1219                 augmented(i, j) = _datas[i * _col + j];  // 
1220                 augmented(i, j + n) = (i == j) ? static_cast<T>(1) : static_cast<T>(0);  // 
1221             }
1222         }
1223         // -
1224         for (uint32_t i = 0; i < n; ++i) {
1225             // 
1226             uint32_t max_row = i;
1227             for (uint32_t k = i + 1; k < n; ++k) {
1228                 if (std::abs(augmented(k, i)) > std::abs(augmented(max_row, i))) {
1229                     max_row = k;
1230                 }
1231             }
1232             // 0
1233             if (std::abs(augmented(max_row, i)) < static_cast<T>(1e-10)) {
1234                 SDL_Log("[ERROR] Matrix is singular and cannot be inverted!");
1235                 return Matrix2D<T>();
1236             }
1237             // 
1238             if (max_row != i) {
1239                 for (uint32_t j = 0; j < 2 * n; ++j) {
1240                     std::swap(augmented(i, j), augmented(max_row, j));
1241                 }
1242             }
1243             // 
1244             T pivot = augmented(i, i);
1245             for (uint32_t j = 0; j < 2 * n; ++j) {
1246                 augmented(i, j) /= pivot;
1247             }
1248             // 
1249             for (uint32_t k = 0; k < n; ++k) {
1250                 if (k != i && std::abs(augmented(k, i)) > static_cast<T>(1e-10)) {
1251                     T factor = augmented(k, i);
1252                     for (uint32_t j = 0; j < 2 * n; ++j) {
1253                         augmented(k, j) -= factor * augmented(i, j);
1254                     }
1255                 }
1256             }
1257         }
1258         // 
1259         for (uint32_t i = 0; i < n; ++i) {
1260             for (uint32_t j = 0; j < n; ++j) {
1261                 _ret(i, j) = augmented(i, j + n);
1262             }
1263         }
1264         return _ret;
1265     }
