1406             void reset(float x, float y, float w, float h, uint16_t border,
1407                        const SDL_Color& border_color, const SDL_Color& background_color) {
1408                 _geometry.reset(x, y, w, h);
1409                 _border_size = border;
1410                 _border_color = border_color;
1411                 _background_color = background_color;
1412                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1413             }
1414             void setGeometry(float x, float y, float w, float h) {
1415                 _geometry.reset(x, y, w, h);
1416             }
1417             void setGeometry(const Vector2& pos, const Size& size) {
1418                 _geometry.reset(pos, size);
1419             }
1420             void setGeometry(const GeometryF& geometry) {
1421                 _geometry = geometry;
1422             }
1423             void setBorderSize(uint16_t border_size) {
1424                 _border_size = border_size;
1425                 S3GF::Algorithm::calcRectangleBorder(_geometry, _border_size, _borders);
1426             }
1427             void setBorderColor(const SDL_Color& color) {
1428                 _border_color = color;
1429             }
1430             void setBackgroundColor(const SDL_Color& color) {
1431                 _background_color = color;
1432             }
1433             const GeometryF& geometry() const { return _geometry; }
1434             uint16_t borderSize() const { return _border_size; }
1435             const SDL_Color& borderColor() const { return _border_color; }
1436             const SDL_Color& backgroundColor() const { return _background_color; }
1437             const SDL_FRect* bordersRect() const { return _borders.data(); }
1438         };
1439 
1440         class Triangle {
1441         public:
1442             explicit Triangle() : _p1(0, 0), _p2(0, 0), _p3(0, 0),
1443                 _border_size(0), _border_color(StdColor::Transparent), _background_color(StdColor::Black) {}
1444             explicit Triangle(float x1, float y1, float x2, float y2, float x3, float y3,
1445                               uint16_t border_size = 0, const SDL_Color& border_color = StdColor::Transparent,
1446                               const SDL_Color& back_color = StdColor::Black)
1447                 : _p1(x1, y1), _p2(x2, y2), _p3(x3, y3),
1448                   _border_size(border_size), _border_color(border_color), _background_color(back_color)
1449                 { updateTri(); updateBorder(); }
1450 
1451             void reset(float x1, float y1, float x2, float y2, float x3, float y3,
1452                        uint16_t border_size, const SDL_Color& border_color, const SDL_Color& back_color) {
1453                 _p1.reset(x1, y1);
1454                 _p2.reset(x2, y2);
1455                 _p3.reset(x3, y3);
1456                 _border_size = border_size;
1457                 _border_color = border_color;
1458                 _background_color = back_color;
1459                 updateTri();
1460                 updateBorder();
1461             }
1462             void reset(const Vector2& pos1, const Vector2& pos2, const Vector2& pos3,
1463                        uint16_t border_size, const SDL_Color& border_color, const SDL_Color& back_color) {
1464                 _p1.reset(pos1);
1465                 _p2.reset(pos2);
1466                 _p3.reset(pos3);
1467                 _border_size = border_size;
1468                 _border_color = border_color;
1469                 _background_color = back_color;
1470                 updateTri();
1471                 updateBorder();
1472             }
1473             void setPosition(uint8_t index, const Vector2& pos) {
1474                 switch (index % 3) {
1475                     case 0:
1476                         _p1.reset(pos);
1477                         break;
1478                     case 1:
1479                         _p2.reset(pos);
1480                         break;
1481                     case 2:
1482                         _p3.reset(pos);
1483                         break;
1484                 }
1485                 updateTri();
1486                 updateBorder();
1487             }
1488             void setBorder(uint16_t border_size, const SDL_Color& color) {
1489                 _border_size = border_size;
1490                 _border_color = color;
1491                 updateBorder();
1492             }
1493             void setBackgroundColor(const SDL_Color& color) {
1494                 _background_color = color;
1495                 updateTri();
1496             }
1497             const Vector2& position(uint8_t index = 0) const {
1498                 switch (index % 3) {
1499                     case 0:
1500                         return _p1;
1501                     case 1:
1502                         return _p2;
1503                     case 2:
1504                         return _p3;
1505                 }
1506             }
1507             uint16_t borderSize() const { return _border_size; }
1508             const SDL_Color& borderColor() const { return _border_color; }
1509             const SDL_Color& backgroundColor() const { return _background_color; }
1510             const int *indices() { return _indices.data(); }
1511             const SDL_Vertex *vertices() { return _vertices.data(); }
1512             const int *borderIndices1() { return _bdi1.data(); }
1513             const int *borderIndices2() { return _bdi2.data(); }
1514             const int *borderIndices3() { return _bdi3.data(); }
1515             const SDL_Vertex *borderVertices1() { return _bd1.data(); }
1516             const SDL_Vertex *borderVertices2() { return _bd2.data(); }
1517             const SDL_Vertex *borderVertices3() { return _bd3.data(); }
1518             size_t borderIndicesCount() const { return _bdi1.size(); }
1519             size_t borderVerticesCount() const { return _bd1.size(); }
1520             size_t indicesCount() const { return _indices.size(); }
1521             size_t vertexCount() const { return _vertices.size(); }
1522         private:
1523             void updateTri() {
1524                 S3GF::Algorithm::calcTriangle(_p1, _p2, _p3, _background_color, _vertices, _indices);
1525             }
1526             void updateBorder() {
1527                 if (_border_size == 0) return;
1528                 S3GF::Algorithm::calcLine(_p1.x, _p1.y, _p2.x, _p2.y, _border_size, _border_color,_bd1, _bdi1);
1529                 S3GF::Algorithm::calcLine(_p2.x, _p2.y, _p3.x, _p3.y, _border_size, _border_color,_bd2, _bdi2);
1530                 S3GF::Algorithm::calcLine(_p1.x, _p1.y, _p3.x, _p3.y, _border_size, _border_color,_bd3, _bdi3);
1531             }
1532             Vector2 _p1;
1533             Vector2 _p2;
1534             Vector2 _p3;
1535             uint16_t _border_size;
1536             SDL_Color _border_color;
1537             SDL_Color _background_color;
1538             std::array<SDL_Vertex, 3> _vertices;
1539             std::array<int, 3> _indices;
1540             std::array<SDL_Vertex, 4> _bd1, _bd2, _bd3;
1541             std::array<int, 6> _bdi1, _bdi2, _bdi3;
1542         };
1543 
1544         struct Ellipse {
1545             Vector2 center_point;
1546             Size radius;
1547             uint16_t border_size;
1548             SDL_Color border_color;
1549             SDL_Color background_color;
1550             uint16_t pts;
1551             std::vector<SDL_FRect> points;
1552         };
1553     }
